namespace L10N\ISO639;


use DomainException;
use IteratorAggregate;
use OutOfBoundsException;

class ISO639 implements IteratorAggregate, DataProvider
{
    const KEY_EN_NAME = 'english_name';
    const KEY_HTTP_NAME = 'http_name';
    const KEY_ISO639_V2 = 'iso_639_2';
    const KEY_ISO639_V1 = 'iso_639_1';

    /**
     * @param $englishName
     * @return mixed
     */
    public function getByEnglishName($englishName)
    {
        if (!preg_match(
            '/^[a-zA-Z]*?$/',
            $englishName
        )
        ) {
            throw new DomainException('Not a valid English Name: ' . $englishName);
        }
        return self::get($englishName);
    }

    /**
     * @param $httpName
     * @return mixed
     */
    public function getByHttpName($httpName)
    {
        if (!preg_match(
            '/^[a-zA-Z]{2}-[a-zA-Z]{2}$/',
            $httpName
        )
        ) {
            throw new DomainException('Not a valid Http Name: ' . $httpName);
        }
        return self::get($httpName);
    }

    /**
     * @param $iso639V1
     * @return mixed
     */
    public function getByISO639V1($iso639V1)
    {
        if (!preg_match(
            '/^[a-z]{2}$/',
            $iso639V1
        )
        ) {
            throw new DomainException('Not a valid ISO 639-1 name' . $iso639V1);
        }
        return self::get($iso639V1);
    }

    /**
     * @param $iso639V2
     * @return mixed
     */
    public function getByISO639V2($iso639V2)
    {
        if (!preg_match(
            '/^[a-z]{3}$/',
            $iso639V2
        )
        ) {
            throw new DomainException('Not a valid ISO 639-2 name' . $iso639V2);
        }
        return self::get($iso639V2);
    }

    /**
     * @api
     * @return array
     */
    public function getAll()
    {
        return $this->languages;
    }

    /**
     * @param string $listBy
     * @return \Generator
     */
    public function listBy($listBy = self::KEY_HTTP_NAME)
    {
        if (!in_array(
            $listBy,
            $keys = [
                self::KEY_ISO639_V2,
                self::KEY_ISO639_V1,
                self::KEY_EN_NAME,
                self::KEY_HTTP_NAME
            ],
            true
        )
        ) {
            throw new DomainException(sprintf('Invalid value for $indexBy, got "%s", expected one of: %s', $listBy,
                                              implode(', ', $keys)));
        }
        foreach ($this->languages as $language) {
            yield $language[$listBy] => $language;
        }
    }

    public function isValid($id){
        return $this->get($id);
    }
    /**
     * @internal
     * @return \Generator
     */
    public function getIterator()
    {
        foreach ($this->languages as $language) {
            yield $language;
        }
    }

    /**
     * @param $id
     * @return mixed
     */
    public function get($id)
    {
        foreach ($this as $language) {
            if (0 === strcasecmp(
                    $id,
                    $language[self::KEY_EN_NAME]
                ) || 0 === strcasecmp(
                    $id,
                    $language[self::KEY_HTTP_NAME]
                ) || 0 === strcasecmp(
                    $id,
                    $language[self::KEY_ISO639_V1]
                ) || 0 === strcasecmp(
                    $id,
                    $language[self::KEY_ISO639_V2]
                )

            ) {
                return $language;
            }
        }
        throw new OutOfBoundsException('ISO 639 does not contain: ' . $id);
    }

